import Link from 'next/link'
import axios from 'axios'

import { useInfiniteQuery } from 'react-query'
import { ReactQueryDevtools } from 'react-query/devtools'

import useIntersectionObserver from '../../hooks/useIntersectionObserver'

function Home() {

  const {
    status,
    data,
    error,
    isFetching,
    isFetchingNextPage,
    isFetchingPreviousPage,
    fetchNextPage,
    fetchPreviousPage,
    hasNextPage,
    hasPreviousPage,
  } = useInfiniteQuery(
    'projects',
    async ({ pageParam = "https://gorest.co.in/public/v1/posts" }) => {
      const res = await axios.get(pageParam)
      return res.data
    },
    {
      getPreviousPageParam: firstPage => {
        return firstPage.meta.pagination.links.previous ?? false;
      },
      getNextPageParam: lastPage => {
        return lastPage.meta.pagination.links.next ?? false;
      },
    }
  )

  const loadMoreButtonRef = React.useRef()

  useIntersectionObserver({
    target: loadMoreButtonRef,
    onIntersect: fetchNextPage,
    enabled: hasNextPage,
  })

  return (
    <div>
      <h1>Infinite Loading</h1>
      {status === 'loading' ? (
        <p>Loading...</p>
      ) : status === 'error' ? (
        <span>Error: {error.message}</span>
      ) : (
        <>
          <div>
            <button
              onClick={() => fetchPreviousPage()}
              disabled={!hasPreviousPage || isFetchingPreviousPage}
            >
              {isFetchingNextPage
                ? 'Loading more...'
                : hasNextPage
                ? 'Load Older'
                : 'Nothing more to load'}
            </button>
          </div>
          {data.pages.map(page => (
            <React.Fragment key={page.meta.pagination.page + 1}>
              {page.data.map(project => {
                console.log(page);
                console.log(project);
                return (
                  <div style={{display: "none"}}>
                <p
                  style={{
                   border: "1px solid black",
                   borderRadius: "4px",
                   display: "block",
                   width: "30vw",
                   margin: "5px auto"
                  }}
                  key={project.id}
                >
                  <p> {project.body}</p>
                 <h5>{project.title}</h5>

                </p>
                </div>
              )})}
            </React.Fragment>
          ))}
          <div>
              <p>
              {isFetchingNextPage
                ? 'Loading more...'
                : hasNextPage
                ? 'Load Newer'
                : 'Nothing more to load'}
            </p>
          </div>
          <div>
            {isFetching && !isFetchingNextPage
              ? 'Background Updating...'
              : null}
          </div>
        </>
      )}
    </div>
  )
}

export default Home;
