import Link from 'next/link'
import axios from 'axios'

import { useInfiniteQuery } from 'react-query'
import { ReactQueryDevtools } from 'react-query/devtools'

import useIntersectionObserver from '../../hooks/useIntersectionObserver'

function Home() {

  const {
    status,
    data,
    error,
    isFetching,
    isFetchingNextPage,

    fetchNextPage,
    hasNextPage,
  } = useInfiniteQuery(
    'projects',
    async ({ pageParam = "https://gorest.co.in/public/v1/posts" }) => {
      const res = await axios.get(pageParam)
      return res.data
    },
    {
      getPreviousPageParam: firstPage => {
        return firstPage.meta.pagination.links.previous ?? false;
      },
      getNextPageParam: lastPage => {
        return lastPage.meta.pagination.links.next ?? false;
      },
    }
  )

  const loadMoreButtonRef = React.useRef()

  useIntersectionObserver({
    target: loadMoreButtonRef,
    onIntersect: fetchNextPage,
    enabled: hasNextPage,
  })

  return (
    <div>
      <h1>Tweets</h1>
      {status === 'loading' ? (
        <></>
      ) : status === 'error' ? (
        <span>Error: {error.message}</span>
      ) : (
        <>
          {data.pages.map(page => (
            <React.Fragment key={page.meta.pagination.page + 1}>
              {page.data.map(project => {

                return (
                  <div>
                <p
                  style={{
                   border: "1px solid black",
                   borderRadius: "4px",
                   display: "block",
                   width: "30vw",
                   margin: "5px auto"
                  }}
                  key={project.id}
                >
                  <span> {project.body}</span>
                 <h5>{project.title}</h5>

                </p>
                </div>
              )})}
            </React.Fragment>
          ))}
          <div>
              <p style={{textAlign : "center"}}   ref={loadMoreButtonRef} onClick={() => fetchNextPage()}>
              {isFetchingNextPage
                ? 'Loading ...'
                : hasNextPage
                ? 'Loading ...'
                : 'Nothing more to load'}
            </p>
          </div>
          <div>
            {isFetching && !isFetchingNextPage
              ? 'Background Updating...'
              : null}
          </div>
        </>
      )}
    </div>
  )
}

export default Home;
